import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function App(){
  const [showform,setShowForm] = useState(false)
  const [updFriends,setUpdFriends] = useState(initialFriends);
  const [splitname,setSplitname] = useState("hi")


  function addFriendhandler(friend){
    setUpdFriends(friends => [...friends,friend])
  }

  // const friends = initialFriends
  function clickhandler(){
    setShowForm((e)=> !e) 
  }

  function eachfriendHandler(e){
    setSplitname(e.name)
  }

  return(
    <div className="app">
      <div className="sidebar">
        <Friendlist friends={updFriends} />
        {showform ? <FormAddFriend  onaddFriend = {addFriendhandler}/> : ""}
        <Button onClick={clickhandler} 
        >{showform ? "Close" : "Add Friend"  }</Button>
      </div>
      <FormSplitBill name={splitname}  />
    </div>
  )
}
export default App;

function Friendlist({friends}){

  return(
    <ul>
      {friends.map((friend) => (<Friends friend = {friend} key = {friend.id} />))}   
    </ul>

  )
}

function Friends({friend}){
  return<li>
    <img src={friend.image} alt={friend.name} />
    <h3>{friend.name}</h3>
    {friend.balance < 0 &&
     (<p className="red">
      You owes {friend.name} ${Math.abs(friend.balance)}
     </p>)}

     {friend.balance > 0 &&
     (<p className="green">
      {friend.name} owes you ${Math.abs(friend.balance)}
     </p>)}

     {friend.balance === 0 &&
     (<p >
      You and {friend.name} are even
     </p>)}
     <Button  >Select</Button> 

  </li>

}



function FormAddFriend({onaddFriend}){
  const [name,setName] = useState ("")
  const [url,setUrl] = useState ("https://i.pravatar.cc/48")

  function addfriend(e){
    e.preventDefault();

    if(!name || !url) return;
    const id = crypto.randomUUID()
    const newFriend =  { 
      name,image:url ,
      balance : 0,id 
    }

    console.log(newFriend)
    onaddFriend(newFriend)
    setName("")
    setUrl("https://i.pravatar.cc/48")

  }
  return(
    <form className="form-add-friend" onSubmit={addfriend}>
      <label>
        Friend Name 
      </label>
          <input type="text" value={name} onChange={(e) =>setName(e.target.value)} />
      <label>
        Image URL 
      </label>
        <input type="text" value={url} onChange={(e) =>setUrl(e.target.value)}/>
        <Button >ADD</Button>
    </form>
  )
}

function Button({children,onClick}){
  return(
    <button className="button" onClick={onClick}>{children}</button>
  )
}

function FormSplitBill({name}){
  return(
    <form className="form-split-bill">
    <h2>SPLIT A BILL WITH {name} </h2>

    <label> bill Value</label>
    <input type="number" />

    <label> Your Expense</label>
    <input type="number" />

    <label> X'S Expense </label>
    <select value="you">
      <option value="you">You</option>
      <option value="friend">X</option>
    </select>


    <Button>Split Bill</Button>

    </form>
  )
}
